  `del_flag` int(2) DEFAULT '0' COMMENT '是否删除,1已删0未删',
  `tenant_id` int(11) DEFAULT '1' COMMENT '租户id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=85 DEFAULT CHARSET=utf8 COMMENT='菜单表';

