mapper.select:
select
  id, role_name, role_code, sort, status, update_time, create_time, del_flag
from sys_role


        and id = #{id}


        and role_name = #{roleName}


        and role_code = #{roleCode}


        and sort = #{sort}


        and status = #{status}


        and update_time = #{updateTime}


        and create_time = #{createTime}


        and del_flag = #{delFlag}
